import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;

public class NettyServer {

    public static void main(String[] args) throws Exception {

        //bossGroupworkerGroupcpu * 2bossGroup,workerGroup
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup(8);

        try {
            //
            ServerBootstrap bootstrap = new ServerBootstrap();
            //
            bootstrap.group(bossGroup, workerGroup) //
                    .channel(NioServerSocketChannel.class) //NioSocketChannel 
                    .option(ChannelOption.SO_BACKLOG, 128) // 
                    .childHandler(new ChannelInitializer<SocketChannel>() {//()
                        //workGroupSocketChannel
                        @Override
                        protected void initChannel(SocketChannel ch) {
                            System.out.println("socketchannel hashcode=" + ch.hashCode()); // SocketChannel channel  NIOEventLoop  taskQueue  scheduleTaskQueue
                            ch.pipeline().addLast(new NettyServerHandler()); // 
                        }
                    });

            System.out.println("..... is ready...");

            //bindisDone()
            ChannelFuture cf = bootstrap.bind(6668).sync();

            //cf 
            cf.addListener((ChannelFutureListener) future -> {
                if (cf.isSuccess()) {
                    System.out.println("");
                } else {
                    System.out.println("");
                }
            });

            //
            cf.channel().closeFuture().sync();
        } finally {
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
        }
    }
}
